alu=''
l1=''
#herv=''
herv=''
sva=''

def findtepos(sequence, TEpos, TEpostotype):
	length=len(sequence)
	random_amount=random.randint(60,200) #how many TEs to insert
	tecount=0

	for i in range(0, random_amount): #insert TE random_amount of times
		tecount += 1
		if tecount == 1:
			te=alu
			testr='Alu'
		elif tecount == 2:
			te=l1
			testr='L1'
		elif tecount== 3:
			te=herv
			testr='HERV'

		elif tecount== 4:
			te=sva
			testr='SVA'

		elif tecount== 5:
			te=alu
			testr='Alu'

		elif tecount== 6:
			te=l1
			testr='L1'
			tecount=0

		random_position=random.randint(0,length)

		lst=[list(range(i-20000,i+20000)) for i in list(TEpos.keys())]
		avoidlist=[item for sublist in lst for item in sublist]

		if random_position not in avoidlist: #insert at random position in chr
			TEpos[random_position]=te
			TEpostotype[random_position]=testr
		else:
			continue

	return TEpos, TEpostotype

def inserttes(sequence, TEpos): # dict with te pos (based on ori len) and which TE inserted

	sortedtepos=sorted(list(TEpos.keys()), reverse=True)
	for randpos in sortedtepos:
		tetype=TEpos[randpos]
		newsequence="{}{}{}".format(sequence[0:randpos],tetype,sequence[randpos:])

		sequence=newsequence

	return sequence

chrline={}
sequence=[]
readnames=[]
tetoreaddict={}

seqs={}
for line in open(sys.argv[1]): # Reference file, extrat reads for simulation

	line = line.rstrip('\n')
	if line.startswith('>'):
		chr=line.split('>')[-1].split(' ')[0]
		seqs[chr]=[]

	else:
		seqs[chr].append(line)


fastaoutput = open(sys.argv[2], 'w')
posoutput=open(sys.argv[3], 'w')
TEdict={}
TEtype={}
for s in seqs:
	chr= s
	print(chr)
	TEdict[chr]={}
	TEtype[chr]={}

	seq=''.join(seqs[s]) #sequence

	randompos=findtepos(seq, TEdict[chr], TEtype[chr]) #insert tes

	newref=inserttes(seq, TEdict[chr])
	teread=newref

	header='>GRCh38_{}'.format(chr)
	fastaoutput.write(header + '\n')
	fastaoutput.write(teread + '\n')

	sortedTEpos=sorted(list(TEdict[chr].keys()))
	for p in sortedTEpos:
		posoutput.write("\t".join([chr, str(p), TEtype[chr][p] ]) + '\n')
